package operators;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 100,num2 = 200;
		int result;
		result = num1+num2;
		System.out.println(result);
		
		result = num1-num2;
		System.out.println(result);
		
		result = num1*num2;
		System.out.println(result);
		
		result = num1/num2;
		System.out.println(result);
		
		result = num1%num2;
		System.out.println(result);
		

	}

}
